package com.kotlin.leetcode.easy

fun main() {
    println(longestPalindrome(""))
}
fun longestPalindrome(s: String): Int {
    val map = HashMap<Char,Int>()
    s.toCharArray().forEachIndexed{i,v->
        map.put(v,map.getOrDefault(v,0)+1)
    }
    var longest = 0
    map.entries.forEach{t ->
        if(t.value%2==0) longest+=t.value else longest+=t.value-1
        if(longest%2==0 && t.value % 2 ==1) longest++
    }
    return longest
}